SELECT u.id_tb_user,u.username,u.email,u.phone,u.petname,u.icon,u.ban,r.id_tb_role,r.role,r.permission FROM ( SELECT id_tb_user,username,email,phone,petname,icon,role,ban FROM tp_user
AND role != #{roleId}
) AS u LEFT JOIN tp_role AS r ON u.role = r.id_tb_role

